<script>
    const appUrl = 'https://www.semanticscholar.org/search?q='
    const appLogo = 'data:image/png;base64,'
    let searchContent = null;
    const placeholder = 'SemanticScholar';
  
    export let cardSettigns;
  
    $: searchUrl = `${appUrl}${searchContent}`;
  
    const goToApp = () => {
      if (searchContent) return window.open(searchUrl);
    }
  
    const onKeyPress = e => {
      if (e.charCode == 13) goToApp();
    };
  </script>
  
  <div class="{cardSettigns.classContainer}">
      <a href="https://www.semanticscholar.org" target="_blank" class="self-center"><img class="{cardSettigns.classImg}" src="{appLogo}" alt="" /></a>
      <div class="p-6 flex flex-col justify-start">
        <div class="flex justify-center">
            <div class="input-group relative flex flex-wrap items-stretch w-full mb-4">
              <input type="search" bind:value="{searchContent}" on:keypress={onKeyPress} class="{cardSettigns.classInput}" placeholder="{placeholder}" aria-label="{placeholder}" aria-describedby="button-addon2">
              <button on:click="{goToApp}" class="{cardSettigns.classButton}" type="button" id="button-addon2">
                <svg aria-hidden="true" focusable="false" data-prefix="fas" data-icon="search" class="w-4" role="img" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 512 512">
                  <path fill="currentColor" d="M505 442.7L405.3 343c-4.5-4.5-10.6-7-17-7H372c27.6-35.3 44-79.7 44-128C416 93.1 322.9 0 208 0S0 93.1 0 208s93.1 208 208 208c48.3 0 92.7-16.4 128-44v16.3c0 6.4 2.5 12.5 7 17l99.7 99.7c9.4 9.4 24.6 9.4 33.9 0l28.3-28.3c9.4-9.4 9.4-24.6.1-34zM208 336c-70.7 0-128-57.2-128-128 0-70.7 57.2-128 128-128 70.7 0 128 57.2 128 128 0 70.7-57.2 128-128 128z"></path>
                </svg>
              </button>
            </div>
        </div>
      </div>
  </div>